import React from 'react';
import Container from './components/Container.js'
import BackgroundImage from './components/BackgroundImage.js'
import BigText from './components/BigText.js'
import Navbar from './components/Navbar.js'
import Card from './components/Card.js'
import Button from './components/Button.js'
import bg from './bg.jpg'
import bg2 from './bg2.jpg'
import styled from 'styled-components';
import portalX_badge from './portalX_badge.png'

const Style = styled.div`
  user-select: none;

  .container {
    display: flex;
    flex-direction: column;
  }

  .infoSection {
    display: flex; flex-direction: row;
    flex-wrap: wrap;
    font-size: 1rem;

    align-items: center;
    justify-content: center;
    .infoPart {
      h1 {
        color: #444;
      }
      width: 200px;
      padding: 5px;
      margin-top: 10px;
      margin-bottom: 30px;
      margin-left: 100px; margin-right: 100px;      
      border-bottom: 4px solid #47FFC4;
    }

    .infoPart:hover {
      border-color: #363636;
    }
  }
`;

function App() {
  return (
    <Style className="App">
      <Navbar>
        <img src={portalX_badge}/>
        <h1>LunaPortal<span className='blue'>X</span></h1>

        <Button>contact</Button>
      </Navbar>
      <div className='container'>

      <Container height='77vh'>
        <BackgroundImage bg={bg2} fixed='true'>
          <BigText align='center' fontsize='3rem'>welcome to 
            <br/>LunaPortalX</BigText>     

            <Button>Start</Button>      

        </BackgroundImage>
      </Container>

      <Container>
      <div className='infoSection'>
        <div className='infoPart'>
          <h1>Experience</h1>
          <p>space like never before.</p>
        </div>
        <div className='infoPart'>
          <h1>Explore</h1>
          <p>parts of the solar system still
            unknown.</p>
        </div>
        <div className='infoPart'>
          <h1>Discover</h1>
          <p>New worlds, people, and technology.</p>
        </div>
      </div>
      </Container>

      <Container height='100vh'>
        <BackgroundImage bg={bg}>
          <BigText align='center' fontsize='3rem'>
            Begin your adventure <br/> this decade
          </BigText>
          <BigText align='center' fontsize='3rem'>
            boarding 2030*
          </BigText>


        </BackgroundImage>
      </Container>
          {/* <Card> */}
          {/*   <p> */}
          {/*   One step gateway to our solar system and beyond */}
          {/*   </p> */}
          {/*   <Button> */}
          {/*     reserve your seat */}
          {/*   </Button> */}
          {/* </Card> */}
      </div>
    </Style>
  );
}

export default App;
