import styled from 'styled-components';

export default styled.div`
	padding: 1em;
  	background: ${props => props.bg ? props.bg : null};
  	border: 1px solid pink;
  	color: #222;

  	text-align: center;
  	font-size: 2rem;
  	font-weight: bold;
`;
