	color: white;
	margin-left: auto;
	margin-right: auto;

	h1 {
		margin-right: auto;


	}

	.row {
		display: flex; 
		flex-direction: row;
		line-height: 15px;
		padding: 20px;
		font-size: 1rem;
		align-items: left;
