import React, {useState} from 'react';
import styled from 'styled-components'
import {Container, BackgroundImage, BigText, Navbar, Card, Button, InfoSelectorBar, InfoSelectorItem, SignUpForm, Img, Signup} from '../components';
import {primary, secondary, third} from '../components/Colors.js'
import bg from '../bg.jpg'; import bg2 from '../bg2.jpg'; import portalX_badge from '../portalX_badge.png'

const Style = styled.div`
  user-select: none;

  .bottomButton {
    margin-top: 40px;
    padding: 10px 50px 10px 50px;
  }

  p {
    font-size: 1.5rem;
  }

  footer {
    background-color: #111;
    .scrollItem {
      margin-left: auto; margin-right: auto;      
      padding-top: 10px;
      padding-bottom: 10px;
      width: 400px;
    }
    p {
      font-size: 0.8rem;
      color: white;
      text-align: center;
      line-height: 10px;
    }
    .email {
      user-select: all;
      color: #47FFC4;
      text-decoration: underline;
    }
  }
`;

function Home(props) {
    const [infoToShow, setInfo] = useState('experience');
  return (
  	<Style>
        <div>
      {/* Top Welcome Page */}
      <Container height='95vh' padding='0px' margin='0px' width='100vw'>
        <BackgroundImage bg={bg2} fixed='true'>
          <Container padding='20px' margin='auto' flexdirection='column'>
          <BigText align='center' fontsize='3rem'>
            welcome to ListX
          </BigText>   
          <p>Dealing with lists is a hassle. ListX eases the strug.</p><br/>
          <a href='#signup'>Try as a guest</a>
          </Container>      
        </BackgroundImage>
      </Container>

      {/* Information Selector */}
      <Container className='scrollItem' margin='0px'>
        <InfoSelectorBar>
        <div className='infoSection'>
          <InfoSelectorItem 
            onClick={()=>{setInfo('experience')}}
            onMouseOver={()=>{setInfo('experience')}}
            active={infoToShow === 'experience' ? true : false}>
            <h1>Experience</h1>
            <p>space like never before.</p>
          </InfoSelectorItem>
          <InfoSelectorItem 
            onClick={()=>{setInfo('explore')}}
            onMouseOver={()=>{setInfo('explore')}}
            active={infoToShow === 'explore' ? true : false}>
            <h1>Explore</h1>
            <p>parts of the solar system still
              unknown.</p>
          </InfoSelectorItem>
          <InfoSelectorItem 
            onClick={()=>{setInfo('discover')}}
            onMouseOver={()=>{setInfo('discover')}}
            active={infoToShow === 'discover' ? true : false}>
            <h1>Discover</h1>
            <p>New worlds, people, and technology.</p>
          </InfoSelectorItem>
        </div>
        </InfoSelectorBar>
      </Container>

      {/* Company Information */}
      <Container height='auto' bg='#111' id='#info' padding='20px 0px 150px 0px' margin='0px'>
          {infoToShow === 'experience' ? 
            <div>
              <BigText align='center' fontsize='3rem'>Why ListX?</BigText>
              <Card align='left'>
                <div className='row'><h2>Manage hundreds of lists easily using CRUD functions</h2></div>
              </Card>
              <Card align='right'>                
                <div className='row'>
                <h2></h2><h2>Sort through the lists and entries effortlessly using search powered by PSQL</h2>
                </div>
              </Card>
              <Card >                
                <div className='row'>
                <h2></h2><h2>Enjoy a better way to view your lists with cards</h2>
                </div>
              </Card>
            </div>            
          : infoToShow === 'explore' ?
            <div>
              <BigText align='center' fontsize='3rem'>Explore</BigText>
              <Card align='left'>
                <div className='row'><h1>Why Lunaportal?</h1></div>
                <div className='row'>
                <h2></h2><h3></h3>
                </div>
              </Card>
              <Card align='right'>
                <div className='row'><h1>Why Lunaportal?</h1></div>
                <div className='row'>
                <h2></h2><h3></h3>
                </div>
              </Card>
              <Card align='left'>
                <div className='row'><h1>Why Lunaportal?</h1></div>
                <div className='row'>
                <h2></h2><h3></h3>
                </div>
              </Card>
            </div>  
            : infoToShow === 'discover' ?
            <div>
              <BigText align='center' fontsize='3rem'>Discover</BigText>
              <Card align='left'>
                <div className='row'><h1>Why Lunaportal?</h1></div>
                <div className='row'>
                <h2></h2><h3></h3>
                </div>
              </Card>
              <Card align='right'>
                <div className='row'><h1>Why Lunaportal?</h1></div>
                <div className='row'>
                <h2></h2><h3></h3>
                </div>
              </Card>             
            </div>              
            :
            'none'
            }
            <div>
          </div>
      </Container>
      {/* logo */}
      <Container height='auto' bg='white' padding='40px 0px 40px 0px' margin='0px'>   
        <div>     
          <Img src={portalX_badge} height='auto' width='auto'/>
        </div>         
      </Container>
      {/* Sign up form */}
      <Container id='signup' height='auto' bg='white' padding='150px 0px 0px 0px'>
        <Signup/>
      </Container>

      <footer>
      <Container  bg='#111' height='auto' margin='0px'>
          <p>Contact @ </p><p className='email' href='menkhus.chris@gmail.com'>ChrisMenkhus</p>
      </Container>
      </footer>
      </div>
    </Style>
  );
}

export default Home;
