import React, {useState} from 'react';
import Container from './components/Container.js'
import BackgroundImage from './components/BackgroundImage.js'
import BigText from './components/BigText.js'
import Navbar from './components/Navbar.js'
import Card from './components/Card.js'
import Button from './components/Button.js'
import bg from './bg.jpg'
import bg2 from './bg2.jpg'
import styled from 'styled-components';
import portalX_badge from './portalX_badge.png'

const Style = styled.div`
  user-select: none;



  .infoSection {
    display: flex; flex-direction: row;
    flex-wrap: wrap;
    font-size: 1rem;

    align-items: center;
    justify-content: center;
    .infoPart {
      h1 {
        color: #444;
      }
      width: 200px;
      padding: 5px;
      margin-top: 10px;
      margin-bottom: 30px;
      margin-left: 100px; margin-right: 100px;      
      border-bottom: 4px solid #47FFC4;
    }

    .infoPart:hover {
      border-color: #363636;
    }
  }
`;

function App() {
  const [infoToShow, setInfo] = useState('experience');

  return (
    <Style className="App">
      <Navbar>
        <img src={portalX_badge}/>
        <h1>LunaPortal<span className='blue'>X</span></h1>

        <Button>contact</Button>
      </Navbar>

      <Container height='81vh'>
        <BackgroundImage bg={bg2} fixed='true'>
          <BigText align='center' fontsize='3rem'>
            welcome to <br/> LunaPortalX
          </BigText>          
        </BackgroundImage>
      </Container>

      <Container>
        <div className='infoSection'>
          <div className='infoPart' onClick={()=>{setInfo('experience')}}>
            <h1>Experience</h1>
            <p>space like never before.</p>
          </div>
          <div className='infoPart' onClick={()=>{setInfo('explore')}}>
            <h1>Explore</h1>
            <p>parts of the solar system still
              unknown.</p>
          </div>
          <div className='infoPart' onClick={()=>{setInfo('discover')}}>
            <h1>Discover</h1>
            <p>New worlds, people, and technology.</p>
          </div>
        </div>
      </Container>

      <Container height='100vh' bg='#111'>
          <BigText align='center' fontsize='3rem'>Experience</BigText>

          {infoToShow === 'experience' ? 
            <div>
              <h1>Trip stats</h1>
              <div>
                <h2>worlds: <span>Moon, Mars, Encellidus, Europa, Saturn.</span></h2>
                <h2>duration: <span>3 years</span></h2>
                <h2>departure: <span>2030</span></h2>
              </div>
            </div>            
          : infoToShow === 'explore' ?
            'explore'
            : infoToShow === 'discover' ?
            'discover'
            :
            'none'
            }
      </Container>
    </Style>
  );
}

export default App;
