import styled from 'styled-components';

export default styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
    justify-content: center;


	border-radius: 10px;
	border: 0px solid black;
	text-align: center;
	color: white;
	margin-left: auto;
	margin-right: auto;

	h1 {
		background-color: #222;
		width: 100%;
		color: red;
	}

	h2 {
		font-size: 1rem;
		margin-right: auto;
	}
	h3 {
		font-size: 1rem;
		margin-right: auto;
	}
	p {
		padding: 20px;
	}
`;
