import React, {useState} from 'react';
import Container from '../components/Container.js'
import styled from 'styled-components'
import Signup from '../components/Signup.js'
import {primary, secondary, third} from '../components/Colors.js'

const Style = styled.div`
	h1 {
		border-bottom: 1px solid white;
		width: 80%;
	}
	ul {
		list-style: inside;
;	}
`;

function Home(props) {
  return (
  	<Style>
    <Container padding='0px' margin='0px auto 20px auto' flexdirection='row' className='cont'>
    	<Container margin='0px auto 0px auto' width='auto'>
    		<Container bg={primary} width='100%'>
    			<h1>Lists But Cards</h1>
    			<p>Dealing with lists is a hassle. Lists But Cards eases the strug. </p>
    			<Container bg={primary} align='left' flexdirection='row' width='100%'>
    				<Container bg={secondary} margin='2px' width='240px' padding='15px 8px 5px 8px' height='200px'>
    					<p>Manage hundreds of lists easily using CRUD functions</p>
    				</Container>
    				<Container bg={secondary} margin='2px' width='240px' padding='15px 8px 5px 8px' height='200px'>
    					<p>Sort through the lists and entries effortlessly using search powered by PSQL</p>
    				</Container>
    				<Container bg={secondary} margin='2px' width='240px' padding='15px 8px 5px 8px' height='200px'>
    					<p>Enjoy a better way to view your lists with cards</p>
    				</Container>
    			</Container>
    		</Container>
    	</Container>
    	<Container margin='0px auto 0px auto'>
    		<Signup signIn={props.signIn} loadUser={props.loadUser} toHome={props.toHome} toLists={props.toLists}/>
    	</Container>
    </Container>
    </Style>
  );
}

export default Home;
