import styled from 'styled-components';

export default styled.div`
  .infoSection {
    display: flex; flex-direction: row;
    flex-wrap: nowrap;
    font-size: 1rem;

    align-items: center;
    justify-content: center;
  }


  @media only screen and (max-width: 600px) { 
    background-color: red;  
  }
`;
