import styled from 'styled-components';

export default styled.div`

      h1 {
        color: #444;
      }

      width: 200px;
      padding: 5px;
      margin-top: 10px;
      margin-bottom: 30px;
      margin-left: 100px; margin-right: 100px;      
      border-bottom: 8px solid #47FFC4;
      border-radius: 3px;
      transition: all 0.5s;

      color: ${props => props.active ? 'green' : 'red'}


    &:hover {
      border-color: #363636;
    }

`;
