import React, {useState} from 'react';
import {Form, Button} from 'react-bootstrap';
import styled from 'styled-components'
import Container from '../components/Container.js'
import {primary, secondary, third} from '../components/Colors.js'

const Style = styled.div`
	a {
		color: ${secondary}
	}
	span {
		color: ${third};
		font-size: 3.0rem;
	}
	.btn-primary {
		background-color: ${secondary};
		border: 0px;
		width: 80%;
		margin-bottom: 10px;
	}
`;


function Login(props) {
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');



  	const handleSubmit = event => {
		fetch('http://localhost:3000/signin', {
			method: 'post',
			headers: {'Content-type': 'application/json'},
			body: JSON.stringify({
				email: email,
				password: password
			})
		})
		.then(response => response.json())
		.then(user => {
			if (user.user_id) {
				props.signIn();
				props.loadUser(user);	
				props.toLists();			
			}
		})	
  	};

  	return (
    <Style className="Login">
    	<Container flexdirection='row-reverse' margin='0px' >
	    	<Container bg={primary} padding='50px'>
		      	<Form>
				  <Form.Group controlId="formBasicEmail">
				    <Form.Label>Email address</Form.Label>
				    <Form.Control onChange={(e)=>{setEmail(e.target.value)}} type="email" placeholder="Enter email"/>
				  </Form.Group>

				  <Form.Group controlId="formBasicPassword">
				    <Form.Label>Password</Form.Label>
				    <Form.Control onChange={(e)=>{setPassword(e.target.value)}} type="password" placeholder="Password" />
				  </Form.Group>
				  <Form.Group controlId="formBasicCheckbox">
				    <Form.Check type="checkbox" label="Stay logged in?" />
				  </Form.Group>
				  <Button variant="primary" onClick={()=>{handleSubmit()}}>
				    Login
				  </Button>
				</Form>
				<p>Already have an account? <a href='/login'>Login</a></p>
			</Container>
    	</Container>
    </Style>
  );
}

export default Login;
