import styled from 'styled-components';

export default styled.div`
	display: flex;
	flex-direction: column;
  	font-size: 9rem;
  	font-weight: bold;
  	color: white;
  	border-bottom: 6px solid white;
  	width: max-content;
  	margin-left: auto; margin-right: auto;
  	padding-top: 15%;
  	justify-self: center;
  	text-align: ${props => props.align ? props.align : center};
`;
