import React from 'react';
import Container from './components/Container.js'
import BackgroundImage from './components/BackgroundImage.js'
import BigText from './components/BigText.js'
import Navbar from './components/Navbar.js'
import bg from './bg.jpg'
import bg2 from './bg2.jpg'
import styled from 'styled-components';

const Style = styled.div`
  .container {
    display: flex;
    flex-direction: column;
  }

  .infoSection {
    display: flex; flex-direction: row;
    font-size: 1rem;

    .infoPart {
      margin-top: 10%;
      margin-left: auto; margin-right: auto;
    }
  }
`;

function App() {
  return (
    <Style className="App">
      <Navbar>
        <h1>LunaPortalX</h1>

        <a href="#" class="myButton">Contact</a>
      </Navbar>
      <div className='container'>
      <Container height='100vh'>
        <BackgroundImage bg={bg2} fixed='true'>
          <BigText align='right' fontsize='3rem'>WELCOME TO 
            <br/>SPACE</BigText>
        </BackgroundImage>
      <div className='infoSection'>
        <div className='infoPart'>
          <h1>Experience</h1>
          <p>For unexperienced and experienced
            passengers alike.</p>
        </div>
        <div className='infoPart'>
          <h1>Explore</h1>
          <p>Go to parts of the solar system still
            left unexplored.</p>
        </div>
        <div className='infoPart'>
          <h1>Discover</h1>
          <p>New worlds, people, and technology awaits.</p>
        </div>
      </div>
      </Container>


      <Container height='100vh'>
        <BackgroundImage bg={bg}>
          <BigText align='center' fontsize='3rem'>For in-experienced and <br/>
          experienced travellers alike <br/>
          LunaPortal will take you to<br/>
          parts of the solar system <br/>
          still left unexplored.<br/>
          Begin your adventure this decade<br/>
          boarding 2030*
          </BigText>
        </BackgroundImage>
      </Container>
      </div>
    </Style>
  );
}

export default App;
