import styled from 'styled-components';

export default styled.div`
    display: flex; flex-direction: column;
    flex-wrap: wrap;
    font-size: 1rem;
    background-color: #222;
    color: white;
    margin-bottom: 50px;
    padding-top: 40px;
    padding-bottom: 40px;
    width: 400px;
    margin-left: auto; margin-right: auto;
    border-radius: 3px;

    align-items: center;
    justify-content: center;

    h1 {
    	color: #EFEFEF;
    	font-weight: bold;
    	font-size: 2rem;
    }

    form {

	    .row {
	    	width: 100%;
	    	display: flex; flex-direction: row;
	    	margin-bottom: 20px;
	    }  	
    	label {
    		margin-right: 20px;
    		margin-left: auto;
    	}
    	input {
    		background-color: #333;
    		border: 0px;
    		margin-right: auto;
    		color: white;
    		text-align: center;
    	}
    	Button {
    		margin-left: auto; margin-right: auto;
    		margin-top: 20px;
    		font-size: 1.5rem;
    	}
    }
`;
