import React, {useState, useEffect} from 'react';
import SignUpForm from './SignUpForm.js'
import Button from './Button.js'
import styled from 'styled-components'
import Container from '../components/Container.js'
import {primary, secondary, third} from '../components/Colors.js'

const Style = styled.div`
	a {
		display: inline-block;
		color: ${secondary};
	}
	p {

	}
	.btn-primary {
		background-color: ${secondary};
		border: 0px;
		margin-bottom: 10px;
		width: 80%;
	}
`;


function Signup(props) {
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [guest, setGuest] = useState(false);

  	const handleSubmit = event => {
		fetch('http://localhost:3000/register', {
			method: 'post',
			headers: {'Content-type': 'application/json'},
			body: JSON.stringify({
				email: email,
				password: password,
				guest: guest
			})
		})
		.then(response => response.json())
		.then(user => {
			if (user.user_id) {
				props.signIn();
				props.loadUser(user);	
				props.toLists();			
			}
		})	
  	};

  	useEffect(()=>{console.log(props)}, [])


  	return (
    <Style className="Signup">
	    <Container bg={primary} padding='50px'>
	    	<SignUpForm>
          	<h1>Join the Empire now</h1>
          	<form>
          	<div>
            <div className='row'>
            <label>Email address</label>
            <input onChange={(e)=>{setEmail(e.target.value)} } type="email" placeholder="Enter email" ></input>
            </div>
            <div className='row'>
            <label>Password</label>
            <input onChange={(e)=>{setPassword(e.target.value)}}></input>
            </div>
            <div className='row'>
            <Button onClick={()=>{ setGuest(true); handleSubmit();}}>Reserve Seat</Button>
            </div>
         	 </div>
          	</form>
        	</SignUpForm>







		      	<Form>
				  <Form.Group controlId="formBasicEmail">
				    <Form.Label>Email address</Form.Label>
				    <Form.Control onChange={(e)=>{setEmail(e.target.value)} } type="email" placeholder="Enter email" />
				    <Form.Text className="text-muted">
				      We'll never share your email with anyone else.
				    </Form.Text>
				  </Form.Group>

				  <Form.Group controlId="formBasicPassword">
				    <Form.Label>Password</Form.Label>
				    <Form.Control onChange={(e)=>{setPassword(e.target.value)} } type="password" placeholder="Password" />
				  </Form.Group>
				  <Form.Group controlId="formBasicCheckbox">
				    <Form.Check type="checkbox" label="Stay logged in?" />
				  </Form.Group>
				  <Button variant="primary" onClick={()=>{handleSubmit()}}>
				    Signup
				  </Button><br/>
				  <Button variant="primary" onClick={()=>{ setGuest(true); handleSubmit();}}>
				    Continue as guest
				  </Button>
				  <p>Already have an account? <a href='/login'>Login</a></p>

				</Form>
		</Container>
    </Style>
  );
}

export default Signup;
