import styled from 'styled-components';

export default styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
    justify-content: center;
	background-color: rgba(255,255,255,0.5);
	height: 250px; width: 400px;
	border: 1px solid black;
	text-align: center;
	color: black;
	margin-left: auto;
	margin-right: auto;


	p {
		padding: 20px;
	}
`;
