import React, {useState, useEffect, useRef} from 'react';
import {Redirect} from 'react-router-dom';
import {Button} from '../components'
import styled from 'styled-components'
import Container from '../components/Container.js'
import {primary} from '../components/Colors.js'

const Style = styled.div`
	color: black;

  h1 {
    font-size: 1.2rem; margin: 0; padding: 0;
    width: 80%;
    border-bottom: 1px solid white;
    margin-bottom: 10px;
  }

  p {
    font-size: 1rem;
    padding: 0px; margin: 4px;
    text-align: left;
    margin-right: auto;
  }

  input {
    font-size: 1rem;
    margin-bottom: 10px;
  }

  Button {
    padding: 2px;
    border-radius: 1px;
    margin-bottom: 10px;
    margin: 20px;
  }

  .list {
    display: flex;
    flex-direction: column;
    padding: 4px;
    width: 100%;
    text-align: left;
    .row {
      display: flex;
      flex-direction: row;
      margin-bottom: 4px;
    }

    Button {      
      margin-left: auto;
      padding: 4px 12px 4px 12px; margin: 0px;
      line-height: 24px;
      margin-right: 2px;
    }
  }
`;

function Lists(props) {
  const [itemsInlist, setItemsInlist] = useState('');
  const [lists, setLists] = useState('');
  const [newItemText, setNewItemText] = useState('');
  const [newListText, setNewListText] = useState('');
  const [selectedList, setSelectedList] = useState('');
  const [selectedListTitle, setSelectedListTitle] = useState('');
  // 503c7d47-20ae-4e1d-9b24-05bcef36a06c

  const createYourListsView = async () => {
  fetch('http://localhost:3000/readlists', {
    method: 'post',
    headers: {'Content-type': 'application/json'},
    body: JSON.stringify({
      user_id: props.user_id,
    })
  })
  .then(response => response.json())
  .then(res => {
    if (res) {
      getJSXfromLists(res).then(data => {
        setLists(data); 
      })
    }
    })  
  };

  const getJSXfromLists = async (res) => {
      return (res.map( (list) => {
        return (
          <div className='row' key={list.list_id}>
            <p>{list.title}</p>
            <Button onClick={()=>{
              removeListFromYourLists(list.list_id);
              }
              }>x</Button>
            <Button onClick={()=>{
              createSelectedListView(list.list_id)}
              }>ğŸ‘</Button>
          </div> 
        )
      }))
  }

  const createSelectedListView = (listToView) => { 
  fetch('http://localhost:3000/readlist', {
    method: 'post',
    headers: {'Content-type': 'application/json'},
    body: JSON.stringify({
      list_id: listToView
    })
  })
  .then(response => response.json())
  .then(res => {
    setSelectedList(res.list_id);
    setSelectedListTitle(res.title);

    let newList = [];
    if (res != null)
    if (res.list_entries) {
      newList = res.list_entries.map((entry) => {
        return (
          <div className='row'>
            <p>{entry}</p>
            <Button onClick={()=>{removeItemFromList(listToView, entry)}}>x</Button>
          </div> 
        )
      })
    } 

    setItemsInlist(newList);  
    })  
  };

  const addListToYourLists = event => {
  fetch('http://localhost:3000/createlist', {
    method: 'post',
    headers: {'Content-type': 'application/json'},
    body: JSON.stringify({
      user_id: props.user_id,
      title: newListText
    })
  })
  .then(response => response.json())
  .then(res => {

     createSelectedListView(res.list_id);
     createYourListsView(); 
  })  
  };

  const removeListFromYourLists = (listToDelete) => {
  fetch('http://localhost:3000/deletelist', {
    method: 'post',
    headers: {'Content-type': 'application/json'},
    body: JSON.stringify({
      list_id: listToDelete
    })
  })
  .then(response => response.json())
  .then(res => {
    createYourListsView();
    console.log(res);
  })    
  }


  const addNewItemToList = (listToView, entry) => {
  fetch('http://localhost:3000/updatelist', {
    method: 'post',
    headers: {'Content-type': 'application/json'},
    body: JSON.stringify({
      list_id: listToView,
      entry: entry
    })
  })
  .then(response => response.json())
  .then(res => {
     console.log(res);
     createSelectedListView(listToView);
  })  
  };

  const removeItemFromList = (listToView, entry) => {
  fetch('http://localhost:3000/updatelist', {
    method: 'post',
    headers: {'Content-type': 'application/json'},
    body: JSON.stringify({
      list_id: listToView,
      entry: entry,
      deleteEntry: true
    })
  })
  .then(response => response.json())
  .then(res => {
    console.log(res.list_entries);
    createSelectedListView(listToView);
  })    
  };


  useEffect(() => {
    createYourListsView();
  }, [props.user_id]);

  return (
    <Style>
      {props.isLogged ? 
        <Container margin='0px' flexdirection='row' height='70vh'>

          <Container className='myLists' margin='20px auto' bg={primary}>
            <h1>Your Lists</h1>
            <p>Create new list</p> 
            <Container flexdirection='row' margin='0px' padding='0px' bg='none'>
              <input type='text' onChange={(e)=>{setNewListText(e.target.value)}} />
            <Button onClick={()=>{ addListToYourLists() }}>Create</Button>
            </Container>
            <div className='list'>
              {lists}
            </div>
          </Container>


          <Container className='selectedListContainer' margin='20px auto' bg={primary}>
            <h1>{selectedListTitle ? selectedListTitle : 'Selected List'}</h1>
            <p>Add new item</p>
            <Container flexdirection='row' margin='0px' padding='0px' bg='none'>
              <input type='text' onChange={(e)=>{setNewItemText(e.target.value)}} />
          	<Button onClick={()=>{
              addNewItemToList(selectedList,newItemText)
              }}>Add</Button>
            </Container>
            <div className='list'>
              {itemsInlist}
            </div>
          </Container>

        </Container>
      :
        <Redirect to='/' exact/>
      }
    </Style>
  );
}
export default Lists;
