export default styled.div`
  box-shadow: 0px 0px 24px 2px rgba(0,0,0,0.2);
  .infoSection {
    display: flex; flex-direction: row;
    flex-wrap: nowrap;
    font-size: 1rem;

    align-items: center;
    justify-content: center;
  }
