import styled from 'styled-components';

export default styled.div`
	display: flex;
	flex-direction: column;
  	font-size: 4rem;
  	font-weight: bold;
  	color: white;
  	border-bottom: 1px solid white;
  	width: max-content;
  	margin-left: auto; margin-right: auto;
  	justify-self: center;
`;
