import React, {useState, useEffect} from 'react';
import {Form, Button} from 'react-bootstrap';
import styled from 'styled-components'
import Container from '../components/Container.js'
import {primary, secondary, third} from '../components/Colors.js'

const Style = styled.div`
	a {
		color: ${secondary}
		font-size: 3rem;
	}
	span {
		color: ${secondary};
		font-size: 3.0rem;
	}
	.btn-primary {
		background-color: ${secondary}
		border: 0px;
	}
`;


function Signup(props) {
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [guest, setGuest] = useState(false);

  	const handleSubmit = event => {
		fetch('http://localhost:3000/register', {
			method: 'post',
			headers: {'Content-type': 'application/json'},
			body: JSON.stringify({
				email: email,
				password: password,
				guest: guest
			})
		})
		.then(response => response.json())
		.then(user => {
			if (user.user_id) {
				props.signIn();
				props.loadUser(user);	
				props.toLists();			
			}
		})	
  	};

  	useEffect(()=>{console.log(props)}, [])


  	return (
    <Style className="Signup">
    	<Container flexdirection='row' margin='0px'>
	    	<Container bg={primary} width='400px'>
	    		<h1>Continue as a <span onClick={()=> {
	    			setGuest(true);
	    			handleSubmit();

	    			}}>Guest!</span></h1>
	    	</Container>
	    	<Container bg={primary}>
		      	<Form>
				  <Form.Group controlId="formBasicEmail">
				    <Form.Label>Email address</Form.Label>
				    <Form.Control onChange={(e)=>{setEmail(e.target.value)} } type="email" placeholder="Enter email" />
				    <Form.Text className="text-muted">
				      We'll never share your email with anyone else.
				    </Form.Text>
				  </Form.Group>

				  <Form.Group controlId="formBasicPassword">
				    <Form.Label>Password</Form.Label>
				    <Form.Control onChange={(e)=>{setPassword(e.target.value)} } type="password" placeholder="Password" />
				  </Form.Group>
				  <Form.Group controlId="formBasicCheckbox">
				    <Form.Check type="checkbox" label="Stay logged in?" />
				  </Form.Group>
				  <Button variant="primary" onClick={()=>{handleSubmit()}}>
				    Signup
				  </Button>
				</Form>
			</Container>
	    	<Container bg={primary} width='400px'>
	    		<h1>Already have an account?<a href='/login'>Login!</a></h1>
	    	</Container>
    	</Container>
    </Style>
  );
}

export default Signup;
